import React from 'react';
import './App.css';
import './data.js';
import './probRangeFinder.js';


/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Variables & Constants                                                     */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
var showCam = false;




/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   React Classes                                                             */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

class Placeholder extends React.Component {
  render() {
    return (
      <div
        id={this.props.id}
        className="placeholder"
        style={{visibility: this.props.visibility ? "visible" : "hidden"}}
      >
        {this.props.content}
      </div>
    );
  }
}

class Video extends React.Component {
  changeSource() {

  }
  render() {
    return (
      <video
        id={this.props.id}
        className=""
        onEnded={() => videoEndHandler()}
        src={this.props.src}
      />
    );
  }
}

class Img extends React.Component {
  render() {
    return (
      <img
        id={this.props.id}
        src={this.props.src}
      />
    );
  }
}

class Btn extends React.Component {
  render() {
    return (
      <button
        type="button"
        id={this.props.id}
        onClick={this.props.onClick}
      >
        {this.props.label}
      </button>
    );
  }
}



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* React Components                                                            */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

const btnbox_playback = (
  <div className="ui-container-h ui-container-gap abs-left">
    <Btn
      id="btn_start"
      label="idle start"
      onClick={() => setNextMotion()}
    />
    <Btn
      id="btn_end"
      label="idle end"
      onClick={() => stopMotion()}
    />
  </div>
);

const btnbox_cam = (
  <div className="ui-container-h ui-container-gap abs-right">
    <Btn
      id="btn_cam"
      label="change cam"
      onClick={() => changeCam()}
    />
  </div>
);

const playback = (
  <input
    type="range"
    className="playback"
    min="0"
    max="100"
  />
);


const footer = (
  <div className="footer">
    {btnbox_playback}
    {btnbox_cam}
    {playback}
  </div>
);



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Output                                                                    */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sideCamVisible: false,
      playStatus: 'none',
      prevMotion: 'none',
      currentMotion: 'none',
      dice: 0,

    };
  }
  throwDice() {
    let _dice = Math.random();
    this.setState({
      dice: _dice
    });
  }
  render() {
    return (
      <div className="App">
        <Placeholder
        id="box_cam_front"
          content={<Img src={require('./img/front_default.jpg')}/>}
          visibility={true}
        />
        <Placeholder
        id="box_cam_side"
          content={<Img src={require('./img/side_default.jpg')}/>}
          visibility={showCam}
        />
        {footer}
      </div>
    );
  }
}
export default App;



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Functions                                                                 */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

function setNextMotion() {
  console.log("set next motion and play");
}

function stopMotion() {
  console.log("stop motion");
}

function changeCam() {
  console.log("change camera");
}

function videoEndHandler() {

}
