import React from 'react';
import './App.css';
import {data_pr} from './data.js';


/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Variables & Constants                                                     */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
var showCam = false;




/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   React classes                                                             */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

// Media related classes -------------------------------------------------------

class Placeholder extends React.Component {
  render() {
    return (
      <div
        id={this.props.id}
        className="placeholder"
        style={{visibility: this.props.visibility ? "visible" : "hidden"}}
      >
        {this.props.content}
      </div>
    );
  }
}

class Video extends React.Component {
  changeSource() {

  }
  render() {
    return (
      <video
        id={this.props.id}
        className=""
        onEnded={() => videoEndHandler()}
        src={this.props.src}
      />
    );
  }
}

class Img extends React.Component {
  render() {
    return (
      <img
        id={this.props.id}
        src={this.props.src}
      />
    );
  }
}

// Dashboard related classes ---------------------------------------------------

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
  }
  makeStatusTable() {

  }
  makeRangeTable(prOut) {
    let rows = [];
    prOut.forEach((item, index) => {
      let ifClass = '';
      ifClass = (index === 0) ? 'border-top' : ifClass;
      /*
      if(index == 0) {
        ifClass = 'border-top';
      }
      */
      if(item.isEndOfCat) {
        if(ifClass) {
          ifClass += ' ';
        }
        ifClass += 'border-bottom';
      }
      if(item.isPrev) {
        if(ifClass) {
          ifClass += ' ';
        }
        ifClass += 'prev';
      }
      if(item.isCurrent) {
        if(ifClass) {
          ifClass += ' ';
        }
        ifClass = 'active';
      }
      let row = <tr key={index} className={ifClass}><td>{item.code}</td><td>{parseFloat(item.pr).toFixed(3)}</td></tr>;
      rows.push(row);
    });
    return rows;
  }
  render() {
    console.log(this.props.prOut);
    let rows = this.makeRangeTable(this.props.prOut);
    return (
      <div className="ui-container-h ui-container-gap abs-left">
      <div className="ui-panel">
        <h1>probability</h1>
