    let _dice = Math.random();
    this.setState({
      dice: _dice
    });
  }

  calcPrOut(prIn, target, prev, current) {
    let _range = [];
    let _cat;
    let _pr_accum = 0;
    let _data = (target < 2) ? prIn.primary : prIn.secondary;
    prIn.motionGroup.forEach((item, index) => {
      _cat = item.cat;
      let pr_cat = _data.find(o => o.cat === _cat).pr;
      let pr_accum_inCat = 0;
      _pr_accum += (index > 0) ? _data[index-1].pr : 0;
      item.motions.forEach((item, index, array) => {
        pr_accum_inCat += pr_cat * item.pr;
        let pr1 = _pr_accum + pr_accum_inCat;
        let isCurrent = (item.code == current) ? true : false;
        let isPrev = (item.code == prev) ? true : false;
        let isEnd = (index == array.length - 1) ? true : false;
        let output = {
          code: item.code,
          pr: pr1,
          cat: _cat,
          isCurrent: isCurrent,
          isPrev: isPrev,
          isEndOfCat: isEnd
        };
        _range.push(output);
      });
    });
    console.log(_range);
    return _range;
  }
  render() {
