import React from 'react';
import './App.css';



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* Variables & Constants                                                       */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
var showCam = false;




/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* React Classes                                                               */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

class Placeholder extends React.Component {
  render() {
    return (
      <div
        id={this.props.id}
        className="placeholder"
      >
        {this.props.content}
      </div>
    );
  }
}

class Video extends React.Component {
  changeSource() {

  }
  render() {
    return (
      <video
        id={this.props.id}
        className=""
        onEnded={() => videoEndHandler()}
        src={this.props.src}
      />
    );
  }
}

class Img extends React.Component {
  render() {
    return (
      <img
        id={this.props.id}
        src={this.props.src}
      />
    );
  }
}

class Btn extends React.Component {
  render() {
    return (
      <button
        type="button"
        id={this.props.id}
        onClick={this.props.onClick}
      >
        {this.props.label}
      </button>
    );
  }
}



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* React Components                                                            */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

const btnbox_playback = (
  <div className="ui-container-h ui-container-gap abs-left">
    <Btn
      id="btn_start"
      label="idle start"
      onClick={() => setNextMotion()}
    />
    <Btn
      id="btn_end"
      label="idle end"
      onClick={() => stopMotion()}
    />
  </div>
);

const btnbox_cam = (
  <div className="ui-container-h ui-container-gap abs-right">
    <Btn
      id="btn_cam"
      label="change cam"
      onClick={() => changeCam()}
    />
  </div>
);

const footer = (
  <div className="footer">
    {btnbox_playback}
    {btnbox_cam}
  </div>
);

/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* Output                                                                      */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

function App() {
  return (
    <div className="App">
      <Placeholder
      id="box_cam_front"
        content={<Img src={require('./img/idle_default.jpg')}/>}
      />
      <Placeholder
      id="box_cam_top"
        content={<Img src={require('./img/idle_default_top.jpg')}/>}
      />
      {footer}
    </div>
  );
}
export default App;



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* Functions                                                                   */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

function setNextMotion() {
  console.log("set next motion and play");
}

function stopMotion() {
  console.log("stop motion");
}

function changeCam() {
  console.log("change camera");
  if (showCam) {
    showCam = false;
  }
  else {
    showCam = true;
  }
}

function videoEndHandler() {

}
