import {data_prob} from './data.js';

function probRangeFinder() {
  let _range_primary = [];
  let _range_secondary = [];
  let _cat;
  let _prob_accum_primary = 0;
  let _prob_accum_secondary = 0;
  let _data_primary = data_prob.primary;
  let _data_secondary = data_prob.secondary;

  data_prob.motionGroup.forEach((item, index) => {
    _cat = item.cat;
    let prob_cat_primary = _data_primary.find(o => o.cat === _cat).prob;
    let prob_cat_secondary = _data_secondary.find(o => o.cat === _cat).prob;
    let prob_accum_inCat_primary = 0;
    let prob_accum_inCat_secondary = 0;
    if(index > 0) {
      _prob_accum_primary += _data_primary[index-1].prob;
      _prob_accum_secondary += _data_secondary[index-1].prob;
    }
    item.motions.forEach((item_each, index_each) => {
      prob_accum_inCat_primary += prob_cat_primary * item_each.prob;
      prob_accum_inCat_secondary += prob_cat_secondary * item_each.prob;
      let prob1 = _prob_accum_primary + prob_accum_inCat_primary;
      let prob2 = _prob_accum_secondary + prob_accum_inCat_secondary;
      let output_primary = {
        code: item_each.code,
        prob: prob1
      };
      let output_secondary = {
        code: item_each.code,
        prob: prob2
      };
      _range_primary.push(output_primary);
      _range_secondary.push(output_secondary);
    });
  });
  console.log(_range_primary);
}

export default probRangeFinder();
