      prOut2: this.calcPrOut(data_pr, 2, prevMotion, currentMotion)
    };
  }

  throwDice() {
    let _dice = Math.random();
    this.setState({
      dice: _dice
    });
  }
  findMotion() {
    let dice = this.state.dice;
    let current = this.state.currentMotion;
    let prev = this.state.prevMotion;
    let temp;
    let fix = '1-a';
    let prOut = (prev === fix) ? this.state.prOut2 : this.state.prOut1;
    prOut.slice().reverse().forEach(item => {
      if (dice >= item.pr) {
        temp = item.code;
        console.log(temp);
        //return
      }
    });
    /*
    while(temp === current && temp != fix) {
      prOut.slice().reverse().forEach(item => {
        (dice >= item.pr)
      });
    }
    */

  }
  calcPrOut(prIn, target, prev, current) {
    let _range = [];
    let _cat;
    let _pr_accum = 0;
    let _data = (target === 1) ?
      prIn.primary.prRatios : prIn.secondary.prRatios;
    prIn.motionGroup.prRatios.forEach((item, index) => {
      _cat = item.cat;
      let pr_cat = _data.find(o => o.cat === _cat).pr;
      let pr_accum_inCat = 0;
      _pr_accum += (index > 0) ? _data[index-1].pr : 0;
      item.prRatios.forEach((item, index, array) => {
        pr_accum_inCat += pr_cat * item.pr;
        let pr1 = _pr_accum + pr_accum_inCat;
        let isCurrent = (item.code === current) ? true : false;
        let isPrev = (item.code === prev) ? true : false;
        let isEnd = (index === array.length - 1) ? true : false;
        let output = {
          code: item.code,
          pr: pr1,
          cat: _cat,
          isCurrent: isCurrent,
          isPrev: isPrev,
          isEndOfCat: isEnd
        };
        _range.push(output);
      });
    });
    console.log(_range);
    return _range;
  }
  setPrOut() {
    const _pr1 = this.calcPrOut(
      this.state.prIn,
      1,
      this.state.prevMotion,
      this.state.currentMotion
    );
    const _pr2 = this.calcPrOut(
      this.state.prIn,
      2,
      this.state.prevMotion,
      this.state.currentMotion
    );
    this.setState({
      prOut1: _pr1,
      prOut2: _pr2
    });
  }
  setPrIn() {
    this.setPrOut();
  }
  handleClick(id) {
    switch(id) {
      case 'btn_start':
        this.throwDice();
        break;
      default:
        break;
    }
  }

  render() {
    let prIn = this.state.prIn;
    let prOut1 = this.state.prOut1;
    let prOut2 = this.state.prOut2;
    this.findMotion();
    return (
