          visibility={showCam}
        />
        <Dashboard
          prOut={this.state.prOut1}
          prevMotion={this.state.prevMotion}
          dice={this.state.dice}
        />
        <Controlboard data={this.state.prIn}/>
        <Footer
          onClick={id => this.handleClick(id)}
        />
      </div>
    );
  }
}
