import React from 'react';
import './App.css';
import {data_pr} from './data.js';


/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Variables & Constants                                                     */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
var showCam = false;




/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   React classes                                                             */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

// Media related classes -------------------------------------------------------

class Placeholder extends React.Component {
  render() {
    return (
      <div
        id={this.props.id}
        className="placeholder"
        style={{visibility: this.props.visibility ? "visible" : "hidden"}}
      >
        {this.props.content}
      </div>
    );
  }
}

class Video extends React.Component {
  changeSource() {

  }
  render() {
    return (
      <video
        id={this.props.id}
        className=""
        onEnded={() => videoEndHandler()}
        src={this.props.src}
      />
    );
  }
}

class Img extends React.Component {
  render() {
    return (
      <img
        id={this.props.id}
        src={this.props.src}
      />
    );
  }
}

// Dashboard related classes ---------------------------------------------------

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
  }
  makeStatusTable() {

  }
  makeRangeTable(prOut) {
    let rows = [];
    prOut.forEach((item, index) => {
      let ifClass;
      if(index == 0) {
        ifClass = 'border-top';
      }
      if(item.isEndOfCat) {
        if(ifClass = null) {
          ifClass += ' ';
        }
        ifClass += 'border-bottom';
      }
      if(item.isPrev) {
        if(ifClass = null) {
          ifClass += ' ';
        }
        ifClass += 'prev';
      }
      if(item.isCurrent) {
        if(ifClass = null) {
          ifClass += ' ';
        }
        ifClass = 'active';
      }
      let row = <tr className={ifClass}><td>{item.code}</td><td>{item.pr}</td></tr>;
      rows.push(row);
    });
    return rows;
  }
  render() {
    console.log(this.props.prOut);
    let rows = this.makeRangeTable(this.props.prOut);
    return (
      <div className="ui-container-h ui-container-gap abs-left">
      <div className="ui-panel">
        <h1>probability</h1>
        <table className="dashboard">
          <tbody id="table_dashboard_random">
            <tr><td>prev</td><td>1-a</td></tr>
            <tr><td>random</td><td>0.333</td></tr>
          </tbody>
        </table>
        <table className="dashboard">
          <tbody id="table_dashboard_pr">
            {rows}
            /*
            <tr className="border-top border-bottom"><td>1-a</td><td>0.555</td></tr>
            <tr><td>1-b</td><td>0.333</td></tr>
            <tr><td>1-c</td><td>0.333</td></tr>
            <tr><td>1-d</td><td>0.333</td></tr>
