  throwDice() {
    let _dice = Math.random();
    this.setState({
      dice: _dice
    });
  }

  calcPrOut(prIn) {
    let _range_1 = [];
    let _range_2 = [];
    let _cat;
    let _pr_accum_1 = 0;
    let _pr_accum_2 = 0;
    let _data_1 = prIn.primary;
    let _data_2 = prIn.secondary;

    prIn.motionGroup.forEach((item, index) => {
      _cat = item.cat;
      let pr_cat_1 = _data_1.find(o => o.cat === _cat).pr;
      let pr_cat_2 = _data_2.find(o => o.cat === _cat).pr;
      let pr_accum_inCat_1 = 0;
      let pr_accum_inCat_2 = 0;
      if(index > 0) {
        _pr_accum_1 += _data_1[index-1].pr;
        _pr_accum_2 += _data_2[index-1].pr;
      }
      item.motions.forEach((item, index, array) => {
        pr_accum_inCat_1 += pr_cat_1 * item.pr;
        pr_accum_inCat_2 += pr_cat_2 * item.pr;
        let pr1 = _pr_accum_1 + pr_accum_inCat_1;
        let pr2 = _pr_accum_2 + pr_accum_inCat_2;
        let isCurrent;
        let isPrev;
        let isEnd = false;
        if(item.code == this.currentMotion) {
          isCurrent = true;
        }
        else {
          isCurrent = false;
        }
        if(item.code == this.prevMotion) {
          isPrev = true;
        }
        else {
          isPrev = false;
        }
        if(index == array.length - 1) {
          isEnd = true;
        }
        let output_1 = {
          code: item.code,
          pr: pr1,
          cat: _cat,
          isCurrent: isCurrent,
          isPrev: isPrev,
          isEndOfCat: isEnd
        };
        let output_2 = {
          code: item.code,
          pr: pr2,
          cat: _cat,
          isCurrent: isCurrent,
          isPrev: isPrev,
          isEndOfCat: isEnd
        };
        _range_1.push(output_1);
        _range_2.push(output_2);
      });
    });
    let output_total = {
      prOut1: _range_1,
      prOut2: _range_2
    }
    console.log(_range_1);
    console.log(_range_2);
    return output_total;
  }

  render() {
    let prIn = this.state.prIn;
    let prOut1 = this.state.prOut1;
    let prOut2 = this.state.prOut2;
    return (
      <div className="App">
        <Placeholder
        id="box_cam_front"
          content={<Img src={require('./img/front_default.jpg')}/>}
          visibility={true}
        />
        <Placeholder
        id="box_cam_side"
          content={<Img src={require('./img/side_default.jpg')}/>}
          visibility={showCam}
        />
        <Dashboard prOut={this.state.prOut1}/>
        <Footer />
      </div>
    );
  }
}
