import React from 'react';
import './App.css';
import {data_pr} from './data.js';


/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Variables & Constants                                                     */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
var showCam = false;




/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   React classes                                                             */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

// Media related classes -------------------------------------------------------

class Placeholder extends React.Component {
  render() {
    return (
      <div
        id={this.props.id}
        className="placeholder"
        style={{visibility: this.props.visibility ? "visible" : "hidden"}}
      >
        {this.props.content}
      </div>
    );
  }
}

class Video extends React.Component {
  changeSource() {

  }
  render() {
    return (
      <video
        id={this.props.id}
        className=""
        onEnded={() => videoEndHandler()}
        src={this.props.src}
      />
    );
  }
}

class Img extends React.Component {
  render() {
    return (
      <img
        id={this.props.id}
        src={this.props.src}
      />
    );
  }
}

// Dashboard related classes ---------------------------------------------------

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
  }
  makeStatusTable() {

  }
  makeRangeTable() {

  }
  render() {
    return (
      <div className="ui-container-h ui-container-gap abs-left">
      <div className="ui-panel">
        <h1>probability</h1>
        <table className="dashboard">
          <tbody id="table_dashboard_random">
            <tr><td>prev</td><td>1-a</td></tr>
            <tr><td>random</td><td>0.333</td></tr>
          </tbody>
        </table>
        <table className="dashboard">
          <tbody id="table_dashboard_prob">
            <tr className="border-top border-bottom"><td>1-a</td><td>0.555</td></tr>
            <tr><td>1-b</td><td>0.333</td></tr>
            <tr><td>1-c</td><td>0.333</td></tr>
            <tr><td>1-d</td><td>0.333</td></tr>
            <tr className="border-top"><td>2-2</td><td>0.333</td></tr>
            <tr className="active"><td>2-3-a</td><td>0.333</td></tr>
            <tr className="prev"><td>2-3-b</td><td>0.333</td></tr>
            <tr className="border-top"><td>3-1-a</td><td>0.333</td></tr>
            <tr><td>3-1-b</td><td>0.333</td></tr>
            <tr><td>3-1-c</td><td>0.333</td></tr>
            <tr className="border-bottom"><td>3-2</td><td>0.333</td></tr>
          </tbody>
        </table>
      </div>
      </div>
    );
  }
}

// Footer ----------------------------------------------------------------------

class Footer extends React.Component {
  render() {
    return (
      <div className="footer">
        {btnbox_playback}
        {btnbox_cam}
        {playback}
      </div>
    );
  }
}

// Generic UI components -------------------------------------------------------

class Btn extends React.Component {
  render() {
    return (
      <button
        type="button"
        id={this.props.id}
        onClick={this.props.onClick}
      >
        {this.props.label}
      </button>
    );
  }
}



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/* React components                                                            */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

const btnbox_playback = (
  <div className="ui-container-h ui-container-gap abs-left">
    <Btn
      id="btn_start"
      label="idle start"
      onClick={() => setNextMotion()}
    />
    <Btn
      id="btn_end"
      label="idle end"
      onClick={() => stopMotion()}
    />
  </div>
);

const btnbox_cam = (
  <div className="ui-container-h ui-container-gap abs-right">
    <Btn
      id="btn_cam"
      label="change cam"
      onClick={() => changeCam()}
    />
  </div>
);

const playback = (
  <input
    type="range"
    className="playback"
    min="0"
    max="100"
  />
);


const footer = (
  <div className="footer">
    {btnbox_playback}
    {btnbox_cam}
    {playback}
  </div>
);



/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */
/*   Output                                                                    */
/* * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * */

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sideCamVisible: false,
      playStatus: 'none',
      prevMotion: 'none',
      currentMotion: 'none',
      dice: 0,

    };
  }
  throwDice() {
    let _dice = Math.random();
    this.setState({
      dice: _dice
    });
  }

  render() {
    return (
      <div className="App">
        <Placeholder
        id="box_cam_front"
          content={<Img src={require('./img/front_default.jpg')}/>}
          visibility={true}
        />
        <Placeholder
        id="box_cam_side"
