function prRangeFinder() {
  let _range_1 = [];
  let _range_2 = [];
  let _cat;
  let _pr_accum_1 = 0;
  let _pr_accum_2 = 0;
  let _data_1 = data_pr.primary;
  let _data_2 = data_pr.secondary;

  data_pr.motionGroup.forEach((item, index) => {
    _cat = item.cat;
    let pr_cat_1 = _data_1.find(o => o.cat === _cat).pr;
    let pr_cat_2 = _data_2.find(o => o.cat === _cat).pr;
    let pr_accum_inCat_1 = 0;
    let pr_accum_inCat_2 = 0;
    if(index > 0) {
      _pr_accum_1 += _data_1[index-1].pr;
      _pr_accum_2 += _data_2[index-1].pr;
    }
    item.motions.forEach((item, index) => {
      pr_accum_inCat_1 += pr_cat_1 * item.pr;
      pr_accum_inCat_2 += pr_cat_2 * item.pr;
      let pr1 = _pr_accum_1 + pr_accum_inCat_1;
      let pr2 = _pr_accum_2 + pr_accum_inCat_2;
      let output_1 = {
        code: item.code,
        pr: pr1
      };
      let output_2 = {
        code: item.code,
        pr: pr2
      };
      _range_1.push(output_1);
      _range_2.push(output_2);
    });
  });
  console.log(_range_1);
  console.log(_range_2);
}
